mapper.select:
select small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and place_of_area=#{placeOfArea}

select small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and taste=#{taste}

select small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and category=#{category}

select small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and goods_price between #{minPrice} and #{maxPrice}
