mapper.select:
select tb_goods_desc.goods_id,small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and tb_goods_desc.is_alive='1' and tb_seller_goods.is_alive='1'and place_of_area=#{placeOfArea}

select tb_goods_desc.goods_id,small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and tb_goods_desc.is_alive='1' and tb_seller_goods.is_alive='1'and taste=#{taste}

select tb_goods_desc.goods_id,small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and tb_goods_desc.is_alive='1' and tb_seller_goods.is_alive='1'and category=#{category}

select tb_goods_desc.goods_id,small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and tb_goods_desc.is_alive='1' and tb_seller_goods.is_alive='1'and goods_price between #{minPrice} and #{maxPrice}

select tb_goods_desc.goods_id,small_pic,goods_title,sell_num,goods_price
from tb_goods_desc,tb_seller_goods
where tb_goods_desc.goods_id=tb_seller_goods.goods_id and tb_goods_desc.is_alive='1' and tb_seller_goods.is_alive='1'and goods_price > #{price}
